<form class="form-horizontal" id="clienteForm">
    <div class="form-group row">
        <label class="control-label col-sm-2" for="Usuario">Usuario:</label>
        <div class="col-sm-10">
            <input type="text" name="Usuario" class="form-control" id="Usuario" placeholder="Ingrese Usuario">
        </div>
    </div>
    <div class="form-group row">
        <label class="control-label col-sm-2" for="rol">Rol:</label>
        <div class="col-sm-10">
            <input type="text" class="form-control" id="rol" placeholder="Ingrese rol" name="rol">
        </div>
    </div>
    <div class="form-group row">
        <label class="control-label col-sm-2" for="saldo">Saldo:</label>
        <div class="col-sm-10">
            <input type="text" class="form-control" id="saldo" placeholder="Ingrese Saldo" name="saldo">
        </div>
    </div>
    <div class="form-group row">
        <label class="control-label col-sm-2" for="email">Email:</label>
        <div class="col-sm-10">
            <input type="email" class="form-control" id="email" placeholder="Ingrese email" name="email">
        </div>
    </div>
    <div class="form-group row">
        <label class="control-label col-sm-2" for="nombre">Nombre:</label>
        <div class="col-sm-10">
            <input type="text" class="form-control" id="nombre" placeholder="Ingrese nombre" name="nombre">
        </div>
        <div class="form-group row">
            <label class="control-label col-sm-2" for="apellido">Apellido:</label>
            <div class="col-sm-10">
                <input type="text" class="form-control" id="apellido" placeholder="Ingrese apellido" name="apellido">
            </div>
        </div>
        <div class="form-group row">
            <label class="control-label col-sm-2" for="contraseña">Contraseña:</label>
            <div class="col-sm-10">
                <input type="text" class="form-control" id="pwd" placeholder="Ingrese Contraseña" name="pwd">
            </div>
        </div>
        <div class="form-group row">
            <div class="col-sm-offset-2 col-sm-10">
                <button type="submit" class="btn btn-primary" id="registro">Registrar cliente</button>
            </div>
        </div>
</form>